# -*- coding:utf-8 -*-
import re


def more_to_one(str_arr, content):
    '''
    把连续的 HTML 标签替换成一个
    :param str_arr:
    :param content:
    :return:
    '''
    for str in str_arr:
        re_sub = '(' + str + ')+'
        content = re.sub(re_sub, str, content, flags=re.I | re.S)
    return content


